def test_release_release1_():
    expect('at+nsocl=0', '')
    receiveAT(1)
    for i in range(12):
        OK('AT+NSOCR="DGRAM",17,14000,1')
        expect('AT+NSOSTF=0,"1.1.1.1",7,0x200,1,"FF"', '+CSCON: 0', 10)
        OK('at+nsocl=0')
        capture(1)


